mapper.insert:
insert into admin (name, username, password,is_super_admin
  )
values (#{name,jdbcType=VARCHAR}, #{username,jdbcType=VARCHAR}, #{password,jdbcType=VARCHAR},#{isSuperAdmin,jdbcType=BIT})

mapper.update:
update admin
set name = #{name,jdbcType=VARCHAR},
  username = #{username,jdbcType=VARCHAR},
  password = #{password,jdbcType=VARCHAR}
  is_super_admin=#{isSuperAdmin,jdbcType=BIT}
where id = #{id,jdbcType=BIGINT}

mapper.select:
select id, name, username, password,is_super_admin
from admin
where id = #{id,jdbcType=BIGINT}

SELECT r.* from role r LEFT JOIN admin_role l ON (l.role_id = r.id) LEFT JOIN role_permission rp on rp.role_id = r.id WHERE l.admin_id = #{adminId}

    SELECT p.resource FROM permission p
    LEFT JOIN role_permission rp ON (rp.p_id = p.id)
    LEFT JOIN admin_role ar ON (ar.role_id = rp.r_id)
    WHERE ar.admin_id = #{adminId}
